def flatten_json(data):
    result = {}
    items = data
    try:
        for item in items:
            for key, value in item.items():
                if key not in result:
                    result[key] = []
                if isinstance(value, list):
                    result[key].extend(value)
                else:
                    result[key].append(value)
    except Exception as e:
        print(f'合并出错{e}')
    return result

